import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import NightsStayIcon from '@material-ui/icons/NightsStay';
import WbSunnyIcon from '@material-ui/icons/WbSunny';

function Navbar() {

    
    let sun = document.getElementById("sun");
    let moons = document.getElementById("moon");
    function moon(){
        document.body.classList.toggle("dark");
        moons.style.display = "none";
        sun.style.display == "flex";
    }
    function suns(){
        document.body.classList.toggle("dark");
        moons.style.display = "flex";
        sun.style.display = "none";
    }


    return (
        <>
            <div className="container">
                <div className="menu">
                    <div className="logo">
                        <h2>ThapaTechnical</h2>
                    </div>
                    <div className="li">
                        <NavLink activeClassName="active" exact to="/">Home</NavLink>
                        <NavLink activeClassName="active" exact to="/service">Services</NavLink>
                        <NavLink activeClassName="active" exact to="/about">About</NavLink>
                        <NavLink activeClassName="active" exact to="/contact">Contact</NavLink>
                    </div>
                    <div onClick={moon} id="moon">
                        <NightsStayIcon />
                    </div>
                    <div style={{display:'none'}} id="sun" onClick={suns}>
                        <WbSunnyIcon />
                    </div>
                </div>
            </div>
        </>
    )
}


export default Navbar;
